    const s = String(pathOrUrl || "").trim();
    if (!s) return null;
    if (s.startsWith("http://") || s.startsWith("https://")) return s;
    if (s.startsWith("/")) return s;
    const base = String(process.env.NEXT_PUBLIC_SUPABASE_URL || "").replace(/\/$/, "");
    if (!base) return null;
    return `${base}/storage/v1/object/public/${s.replace(/^\/+/, "")}`;
  }
  const hero =
    plan.images?.hero_desktop ||
    plan.images?.hero_mobile ||
    plan.images?.hero ||
    "/placeholders/plan-hero.svg";

  const heroUrl = resolvePublicImageUrl(hero) || "/placeholders/plan-hero.svg";
  const gallery0Url =
    resolvePublicImageUrl((plan as any).galleryImages?.[0]) || resolvePublicImageUrl(plan.images?.gallery?.[0]) || heroUrl;
  const gallery1Url =
    resolvePublicImageUrl((plan as any).galleryImages?.[1]) || resolvePublicImageUrl(plan.images?.gallery?.[1]) || gallery0Url;
  const floor0Url =
    resolvePublicImageUrl((plan as any).floorplanImages?.[0]) ||
