          {project.planDetailsUrl ? (
            <div className="mt-3">
              <Link href={project.planDetailsUrl} className="text-sm font-semibold text-orange-600 underline">
                Click here for plan details
              </Link>
            </div>
          ) : null}
        </div>
      </Reveal>

      {videoFileUrl || videoUrl ? (
        <Reveal delayMs={80}>
          <div className="mt-8 overflow-hidden rounded-xl border border-neutral-200 bg-white">
            <div className="relative w-full" style={{ paddingTop: "56.25%" }}>
              {videoFileUrl ? (
                <video
                  src={videoFileUrl}
                  controls
                  className="absolute inset-0 h-full w-full object-cover"
                />
              ) : (
                <iframe
                  src={getVideoEmbedUrl(videoUrl ?? "")}
