      <div className="mt-8 grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
        {project.gallery.map((img, idx) => (
          <Reveal key={idx} delayMs={(idx % 6) * 60}>
            <GalleryTile item={img} />
          </Reveal>
        ))}
      </div>
    </main>
  );
}

