"use client";

import { useEffect, useMemo, useState } from "react";

import { usePlan } from "@/lib/planContext";
import { PdpProvider, type PlanSetConfig, type PlanSetId, usePdp } from "@/lib/pdpState";
import { useCart } from "@/lib/cart/CartContext";

import { InteriorHeader } from "@/sections/InteriorHeader";
import { MobileFooter } from "@/sitePages/home/mobile/MobileFooter";
import { PlanImage } from "@/components/media/PlanImage";
import { ImageLightbox } from "@/components/ImageLightbox";
import { faqSections } from "@/content/faq";

import iconBed from "../../assets/Final small icon images black svg/Icon bed-black.svg";
import iconBath from "../../assets/Final small icon images black svg/Icon bath-black.svg";
import iconGarage from "../../assets/Final small icon images black svg/Icon garage-black.svg";

type RelatedPlan = {
  slug: string;
  name: string;
  heated_sqft: number;
  beds: number | null;
  baths: number | null;
  stories: number | null;
  garage_bays: number | null;
  cardImages: { front: string; plan: string };
};

function resolvePublicImageUrl(pathOrUrl: string | null | undefined) {
  const s = String(pathOrUrl || "").trim();
  if (!s) return null;
  if (s.startsWith("http://") || s.startsWith("https://")) return s;
  if (s.startsWith("/")) return s;
  const base = String(process.env.NEXT_PUBLIC_SUPABASE_URL || "").replace(/\/$/, "");
  if (!base) return null;
  return `${base}/storage/v1/object/public/${s.replace(/^\/+/, "")}`;
}

function formatUsdFromCents(cents: number | null | undefined) {
  const dollars = Math.round((Number(cents) || 0) / 100);
  return `$${dollars.toLocaleString()}`;
}

function startingPriceUsdFallback(heatedSqft: number) {
  const price = 1250 + 0.65 * heatedSqft;
  return `$${Math.round(price).toLocaleString()}`;
}

function imgSrc(mod: unknown): string {
  return (mod as any)?.src ?? (mod as any);
}

function MobileMetaChip(props: { iconSrc: string; label: string }) {
  return (
    <div className="inline-flex items-center gap-2 rounded-full border border-stone-200 bg-white px-3 py-2 text-[12px] font-semibold text-zinc-900">
      <img src={props.iconSrc} alt="" aria-hidden="true" className="h-4 w-4" draggable={false} />
      <span>{props.label}</span>
    </div>
  );
}

function MobileAddToCartButton(props: { className?: string }) {
  const plan = usePlan();
  const { addItem } = useCart();
  const { selections, planSetOptions, canAddToCart } = usePdp();

  const planSet = planSetOptions.find((o) => o.id === selections.planSetId) ?? null;

  function onClick() {
    if (!canAddToCart || !planSet) {
      window.alert("Please select a Plan Set.");
      return;
    }

    addItem({
      slug: plan.slug,
      name: plan.name,
      heated_sqft: plan.heated_sqft,
      license_type: planSet.license,
      addons: {
        ...(planSet.addOns ?? {}),
        ...(selections.addOns ?? {}),
      },
      rush: false,
      paper_sets: planSet.paperSets,
      qty: 1,
    });

    fetch("/api/events", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ event_name: "add_to_cart", plan_slug: plan.slug }),
    }).catch(() => {
      // ignore
    });
  }

  return (
    <button
      type="button"
      onClick={onClick}
      disabled={!canAddToCart}
      className={
        "inline-flex h-[52px] w-full items-center justify-center rounded-full bg-orange-600 text-[14px] font-semibold text-white disabled:cursor-not-allowed disabled:opacity-50 " +
        (props.className || "")
      }
    >
      Add to cart
    </button>
  );
}

function ProductDetailsMobileInner() {
  const plan = usePlan();
  const { planSetOptions, selections, setPlanSetId, toggleAddOn, quote, quoteLoading, builderCode } = usePdp();

  const hero =
    plan.images?.hero_mobile ||
    plan.images?.hero_desktop ||
    plan.images?.hero ||
    plan.images?.gallery?.[0] ||
    (plan as any).galleryImages?.[0] ||
    "/placeholders/plan-hero.svg";

  const images = useMemo(() => {
    const gResolved = Array.isArray((plan as any).galleryImages) ? (((plan as any).galleryImages ?? []) as any[]) : [];
    const fpResolved = Array.isArray((plan as any).floorplanImages) ? (((plan as any).floorplanImages ?? []) as any[]) : [];
    const gLegacy = Array.isArray(plan.images?.gallery) ? (plan.images?.gallery ?? []) : [];
    const fpLegacy = Array.isArray(plan.images?.floorplan) ? (plan.images?.floorplan ?? []) : [];
    const raw = [hero, ...gResolved, ...fpResolved, ...gLegacy, ...fpLegacy];
    const resolved = raw.map(resolvePublicImageUrl).filter(Boolean) as string[];
    const unique = Array.from(new Set(resolved));
    return unique.length > 0 ? unique : ["/placeholders/plan-hero.svg"];
  }, [hero, (plan as any).galleryImages, (plan as any).floorplanImages, plan.images?.gallery, plan.images?.floorplan]);

  const [activeImage, setActiveImage] = useState<string>(images[0] || "/placeholders/plan-hero.svg");
  useEffect(() => {
    setActiveImage(images[0] || "/placeholders/plan-hero.svg");
  }, [images]);

  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);

  const includedSection = faqSections.find((s) => s.title === "Whatâ€™s Included (and Whatâ€™s Not)");
  const included = includedSection?.items.find((i) => i.question.includes("included"));

  const [planSetPrices, setPlanSetPrices] = useState<Record<string, number>>({});
  useEffect(() => {
    let cancelled = false;

    async function quoteSubtotalCents(input: { license: "single" | "builder"; paperSets: number; addOns: any }) {
      const res = await fetch("/api/quote", {
        method: "POST",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          items: [{ slug: plan.slug, license: input.license, paperSets: input.paperSets, addOns: input.addOns, qty: 1 }],
          builderCode: builderCode || undefined,
        }),
      });
      const json = (await res.json().catch(() => null)) as any;
      if (!res.ok) throw new Error(String(json?.error ?? "Failed to quote"));
      return Number(json?.subtotalCents ?? 0);
    }

    async function run() {
      try {
        const next: Record<string, number> = {};
        for (const o of planSetOptions) {
          const cents = await quoteSubtotalCents({ license: o.license, paperSets: o.paperSets, addOns: { ...(o.addOns ?? {}) } });
          next[o.id] = cents;
        }
        if (!cancelled) setPlanSetPrices(next);
      } catch {
        if (!cancelled) setPlanSetPrices({});
      }
    }

    run();
    return () => {
      cancelled = true;
    };
  }, [builderCode, plan.slug, planSetOptions]);

  const metaSqft = `${plan.heated_sqft.toLocaleString()} sq ft`;
  const beds = plan.beds ?? null;
  const baths = plan.baths ?? null;
  const garages = plan.garage_bays ?? null;

  const floorplanImages = useMemo(() => {
    const fpResolved = Array.isArray((plan as any).floorplanImages) ? (((plan as any).floorplanImages ?? []) as any[]) : [];
    const fpLegacy = Array.isArray(plan.images?.floorplan) ? (plan.images?.floorplan ?? []) : [];
    const out = [...fpResolved, ...fpLegacy].map(resolvePublicImageUrl).filter(Boolean) as string[];
    return Array.from(new Set(out));
  }, [(plan as any).floorplanImages, plan.images?.floorplan]);

  const [related, setRelated] = useState<RelatedPlan[]>([]);
  useEffect(() => {
    let mounted = true;
    fetch("/api/catalog/plans")
      .then(async (r) => {
        const j = await r.json();
        if (!r.ok) throw new Error(j.error || "Failed to load");
        return j;
      })
      .then((j) => {
        if (!mounted) return;
        const rows = ((j.plans ?? []) as RelatedPlan[]).filter((p) => p.slug !== plan.slug);
        setRelated(rows.slice(0, 6));
      })
      .catch(() => {
        if (!mounted) return;
        setRelated([]);
      });
    return () => {
      mounted = false;
    };
  }, [plan.slug]);

  const currentPrice = quote?.subtotalCents ? formatUsdFromCents(quote.subtotalCents) : startingPriceUsdFallback(plan.heated_sqft);

  return (
    <div className="bg-white text-zinc-800 font-gilroy">
      <InteriorHeader />

      <ImageLightbox
        open={lightboxOpen}
        images={images}
        index={lightboxIndex}
        onClose={() => setLightboxOpen(false)}
        onIndexChange={setLightboxIndex}
      />

      <main className="px-4 pt-4 pb-[120px]">
        <div className="min-[430px]:grid min-[430px]:grid-cols-[1fr_92px] min-[430px]:gap-3">
          <button
            type="button"
            aria-label="Open image"
            onClick={() => {
              const idx = Math.max(0, images.findIndex((s) => s === activeImage));
              setLightboxIndex(idx);
              setLightboxOpen(true);
            }}
            className="overflow-hidden rounded-[20px] border border-neutral-200 bg-neutral-100"
          >
            <PlanImage src={activeImage} alt="" sizes="(max-width: 767px) 100vw, 50vw" className="rounded-[20px]" />
          </button>

          <div className="mt-3 min-[430px]:mt-0">
            <div className="flex gap-2 overflow-x-auto pb-1 min-[430px]:flex-col min-[430px]:overflow-visible">
              {images.slice(0, 8).map((src) => {
                const active = src === activeImage;
                return (
                  <button
                    key={src}
                    type="button"
                    onClick={() => setActiveImage(src)}
                    className={
                      "shrink-0 overflow-hidden rounded-[14px] border bg-neutral-100 " +
                      (active ? "border-orange-600" : "border-neutral-200")
                    }
                    aria-label="Select image"
                  >
                    <img src={src} alt="" className="h-[64px] w-[64px] object-cover" draggable={false} />
                  </button>
                );
              })}
            </div>
          </div>
        </div>

        <section className="mt-5">
          <div className="text-[22px] leading-[28px] font-semibold text-neutral-900">{plan.name}</div>
          <div className="mt-1 text-[13px] leading-[18px] text-neutral-600">{metaSqft}</div>

          <div className="mt-3 flex flex-wrap gap-2">
            <MobileMetaChip iconSrc={imgSrc(iconBed)} label={`${beds ?? "-"} Beds`} />
            <MobileMetaChip iconSrc={imgSrc(iconBath)} label={`${baths ?? "-"} Baths`} />
            <MobileMetaChip iconSrc={imgSrc(iconGarage)} label={`${garages ?? "-"} Garage`} />
          </div>
        </section>

        <section className="mt-6 rounded-[18px] border border-neutral-200 bg-white p-4">
          <div className="flex items-baseline justify-between gap-3">
            <div className="text-[12px] font-semibold text-neutral-500">PRICE</div>
            <div className="text-[12px] text-neutral-500">{quoteLoading ? "Updatingâ€¦" : ""}</div>
          </div>
          <div className="mt-1 text-[28px] leading-[32px] font-semibold text-orange-600">{currentPrice}</div>

          <div className="mt-4">
            <div className="text-[12px] font-semibold text-neutral-500">PLAN SET</div>
            <div className="mt-2 flex flex-col gap-2">
              {planSetOptions.map((o: PlanSetConfig) => {
                const selected = selections.planSetId === o.id;
                const price = planSetPrices[o.id];
                return (
                  <button
                    key={o.id}
                    type="button"
                    className={
                      "flex items-center justify-between rounded-[16px] border px-4 py-3 text-left " +
                      (selected ? "border-orange-600 bg-orange-50" : "border-neutral-200 bg-white")
                    }
                    onClick={() => setPlanSetId(o.id as PlanSetId)}
                  >
                    <div className="min-w-0">
                      <div className="text-[14px] font-semibold text-neutral-900">{o.label}</div>
                      <div className="mt-1 text-[12px] text-neutral-600">{o.license === "builder" ? "Unlimited build" : "Single build"}</div>
                    </div>
                    <div className="shrink-0 pl-3 text-[14px] font-semibold text-orange-600">{price ? formatUsdFromCents(price) : ""}</div>
                  </button>
                );
              })}
            </div>
          </div>

          <div className="mt-4">
            <div className="text-[12px] font-semibold text-neutral-500">ADD-ONS</div>
            <div className="mt-2 flex flex-col gap-2">
              {[
                { key: "readableReverse" as const, label: "Readable Reverse" },
                { key: "minorChanges" as const, label: "Minor Changes" },
                { key: "sitePlan" as const, label: "Site Plan" },
              ].map((a) => (
                <button
                  key={a.key}
                  type="button"
                  onClick={() => toggleAddOn(a.key)}
                  className={
                    "flex items-center justify-between rounded-[16px] border px-4 py-3 " +
                    (selections.addOns?.[a.key] ? "border-orange-600 bg-orange-50" : "border-neutral-200 bg-white")
                  }
                >
                  <div className="text-[14px] font-semibold text-neutral-900">{a.label}</div>
                  <div className={"text-[12px] font-semibold " + (selections.addOns?.[a.key] ? "text-orange-600" : "text-neutral-400")}
                  >
                    {selections.addOns?.[a.key] ? "Selected" : "Optional"}
                  </div>
                </button>
              ))}
            </div>
          </div>
        </section>

        <section className="mt-8">
          <div className="text-[16px] leading-[20px] font-semibold text-neutral-900">Description</div>
          <div className="mt-2 text-[14px] leading-[20px] text-neutral-700">
            {String(plan.description || "").trim() || "Description coming soon."}
          </div>
        </section>

        {included?.answer ? (
          <section className="mt-8">
            <div className="text-[16px] leading-[20px] font-semibold text-neutral-900">Whatâ€™s included</div>
            <div className="mt-2 whitespace-pre-line text-[14px] leading-[20px] text-neutral-700">{included.answer}</div>
          </section>
        ) : null}

        <section className="mt-8">
          <div className="text-[16px] leading-[20px] font-semibold text-neutral-900">Floor plans</div>
          <div className="mt-3 flex flex-col gap-4">
            {floorplanImages.length === 0 ? (
              <div className="rounded-[18px] border border-neutral-200 bg-neutral-50 p-4 text-[14px] text-neutral-600">
                Floor plan drawings coming soon.
              </div>
            ) : (
              floorplanImages.map((src) => (
                <button
                  key={src}
                  type="button"
                  className="overflow-hidden rounded-[18px] border border-neutral-200 bg-neutral-100"
                  onClick={() => {
                    const idx = Math.max(0, images.findIndex((s) => s === src));
                    setLightboxIndex(idx);
                    setLightboxOpen(true);
                  }}
                >
                  <img src={src} alt="Floor plan" className="w-full object-cover" draggable={false} />
                </button>
              ))
            )}
          </div>
        </section>

        {related.length > 0 ? (
          <section className="mt-10">
            <div className="text-[18px] leading-[22px] font-semibold text-neutral-900">Trending now</div>
            <div className="mt-4 flex flex-col gap-6">
              {related.map((p) => (
                <div key={p.slug} className="rounded-[20px] border border-neutral-200 bg-white overflow-hidden">
                  <a href={`/house/${p.slug}`} className="block">
                    <div className="h-[200px] bg-neutral-100">
