                        <MobileMetaChip iconSrc={imgSrc(iconBath)} label={`${p.baths ?? "-"} Baths`} />
                        <MobileMetaChip iconSrc={imgSrc(iconGarage)} label={`${p.garage_bays ?? "-"} Garage`} />
                      </div>
                    </div>
                  </a>
                </div>
              ))}
            </div>
          </section>
        ) : null}

        <div className="mt-10">
          <MobileFooter />
        </div>
      </main>

      <div className="fixed inset-x-0 bottom-0 z-40 border-t border-neutral-200 bg-white">
        <div className="px-4 pt-3" style={{ paddingBottom: "calc(env(safe-area-inset-bottom) + 12px)" }}>
          <div className="flex items-center justify-between gap-3">
            <div>
              <div className="text-[12px] text-neutral-500">Total</div>
              <div className="text-[16px] font-semibold text-neutral-900">{currentPrice}</div>
            </div>
            <div className="flex-1">
              <MobileAddToCartButton />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

